}

interface Seller {
  id: string;
  name: string;
  ifFlipkartAssured: boolean;
}

interface Discount {
  type: DiscountType;
  value: number;
}

interface Product {
  id: string;
  name: string;
  description: string;
  seller: Seller;
  amount: number;
  discount: Discount;
  noOfStocksAvailable: number;
}






