        seller: {id:"1",
          name:"Mythonglory retail",
          ifFlipkartAssured: true,
        },
        amount: 350,
        discount: {
         type : DiscountType.PERCENTAGE,            

         value: 550,
        },
         noOfStocksAvailable: 5,
      }
] 
  return(    
       <div className="main">
          <div className="container1">
             <div className="header">
                 <div className="flipkart">Flipkart</div>
                 <div className="grocery">Grocery</div>
             </div>
             <div className="address">Address</div>

             <div className="productDetails">
               {products.map(({id,...otherProductProps}) =>(
                <Products key={id} {...otherProductProps}/>
               ))}

             </div>
